import{_ as r,c as a,a8 as d,o as e}from"./chunks/framework.D3mxl-L2.js";const o="/image/pe/pe01.png",n="/image/pe/pe02.png",s="/image/pe/pe03.png",p="/image/pe/pe04.png",l="/image/pe/pe05.png",g="/image/pe/pe06.png",i="/image/pe/pe07.png",c="/image/pe/pe08.jpg",m="/image/pe/pe09.jpg",h="/image/pe/pe10.jpg",x=JSON.parse('{"title":"制作PE系统","description":"","frontmatter":{"title":"制作PE系统"},"headers":[],"relativePath":"initall/pe/pe.md","filePath":"initall/pe/pe.md","lastUpdated":1745343323000}'),b={name:"initall/pe/pe.md"};function F(E,t,_,f,P,u){return e(),a("div",null,t[0]||(t[0]=[d('<h1 id="制作pe系统" tabindex="-1">制作PE系统 <a class="header-anchor" href="#制作pe系统" aria-label="Permalink to &quot;制作PE系统&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">PE系统是什么？</p><p>PE系统是一个装在U盘或者硬盘里面的一个小小的引导系统,当你的电脑出现问题无法正常进入系统，这时候就可以插入你的PEU盘通过BIOS设置启动PE系统，在PE系统里面尝试修复系统或者重装系统，内置了许多的工具，也可以帮你优化你的电脑</p></div><h2 id="准备工具" tabindex="-1">准备工具 <a class="header-anchor" href="#准备工具" aria-label="Permalink to &quot;准备工具&quot;">​</a></h2><ul><li><p>Win10/11系统电脑一台（可以正常使用的电脑）</p></li><li><p>8GB以上的U盘或者硬盘一个（制作PE系统会格式化磁盘请提前备份好文件）</p></li></ul><h2 id="开始制作" tabindex="-1">开始制作 <a class="header-anchor" href="#开始制作" aria-label="Permalink to &quot;开始制作&quot;">​</a></h2><h3 id="第一步" tabindex="-1">第一步 <a class="header-anchor" href="#第一步" aria-label="Permalink to &quot;第一步&quot;">​</a></h3><p>前往 <a href="https://www.wepe.com.cn/" target="_blank" rel="noreferrer">微PE官网 https://www.wepe.com.cn/</a> 下载最新版制作工具 <img src="'+o+'" alt="" data-fancybox="gallery"> 如果提示需要捐赠就点击旁边的<strong>先不捐赠</strong><img src="'+n+'" alt="" data-fancybox="gallery"><img src="'+s+'" alt="" data-fancybox="gallery"></p><h3 id="第二步" tabindex="-1">第二步 <a class="header-anchor" href="#第二步" aria-label="Permalink to &quot;第二步&quot;">​</a></h3><p>将U盘插入电脑，打开下载好的程序，点击右下角的USB图标 <img src="'+p+'" alt="" data-fancybox="gallery"> 这里默认就行，<strong>点击立即安装PE到U盘，提示请务必提前备份数据并关闭安全软件</strong>没问题就点击<strong>开始制作</strong><img src="'+l+'" alt="" data-fancybox="gallery"> PE系统体积比较小巧，一般情况下一分钟左右就安装完成了 <img src="'+g+'" alt="" data-fancybox="gallery"> 安装完成，退出U盘测试一下 <img src="'+i+'" alt="" data-fancybox="gallery"></p><h2 id="安装完成" tabindex="-1">安装完成 <a class="header-anchor" href="#安装完成" aria-label="Permalink to &quot;安装完成&quot;">​</a></h2><p>我们需要在电脑关机的时候插入PE系统U盘，接着开机一直点按快捷键进入电脑的BIOS（各品牌电脑常用的BIOS快捷键已列在下表了）选择使用PE系统引导运行电脑</p><table tabindex="0"><thead><tr><th>品牌</th><th>笔记本电脑 BIOS 启动快捷键</th><th>台式机 BIOS 启动快捷键</th></tr></thead><tbody><tr><td><strong>联想 (Lenovo)</strong></td><td>F1 或 F2</td><td>F1 或 F2</td></tr><tr><td><strong>惠普 (HP)</strong></td><td>F10</td><td>F10</td></tr><tr><td><strong>戴尔 (Dell)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>华硕 (ASUS)</strong></td><td>F2</td><td>DEL 或 F2</td></tr><tr><td><strong>宏碁 (Acer)</strong></td><td>F2</td><td>DEL 或 F2</td></tr><tr><td><strong>东芝 (Toshiba)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>索尼 (Sony)</strong></td><td>F1 或 F2 或 F3</td><td>F1 或 F2 或 F3</td></tr><tr><td><strong>三星 (Samsung)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>苹果 (Apple)</strong></td><td>按住 Option 键</td><td>N/A</td></tr><tr><td><strong>微星 (MSI)</strong></td><td>DEL</td><td>DEL</td></tr><tr><td><strong>技嘉 (GIGABYTE)</strong></td><td>DEL</td><td>DEL</td></tr><tr><td><strong>华擎 (ASRock)</strong></td><td>DEL</td><td>DEL</td></tr><tr><td><strong>英特尔 (Intel)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>神舟 (Hasee)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>方正 (Founder)</strong></td><td>DEL</td><td>DEL</td></tr><tr><td><strong>清华同方 (Tongfang)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>海尔 (Haier)</strong></td><td>F2</td><td>F2</td></tr><tr><td><strong>明基 (BenQ)</strong></td><td>F2</td><td>F2</td></tr></tbody></table><p>这里就用华硕天选三做演示 按下电源键开机 一直按 F2 快捷键进入BIOS 按下 F8 选择使用 U盘启动 回车确认由</p><blockquote><p>于大部分电脑的Biso都是英文界面，但还有少部分支持中文语言切换，如果看不懂英文的话可以用翻译软件拍照翻译辅助完成操作 例如：微信-扫一扫-翻译</p></blockquote><p><img src="'+c+'" alt="" data-fancybox="gallery"><img src="'+m+'" alt="" data-fancybox="gallery"> 进入系统后可以看见系统非常的简洁，内置了许多的系统调试工具 <img src="'+h+'" alt="" data-fancybox="gallery"> 我这里使用的是一个64GB的优盘操作，PE安装完成后还剩下57GB可以当普通U盘使用，当正常插入电脑的时候是不会显示PE系统分区的。</p><p>此时我们也可以将我们常用的一些系统镜像（win10 Win11）放在这个U盘里面，当电脑出现无法进入系统的时候就可以直接使用PE系统来重装系统，关于PE系统的详细使用说明可以前往官网了解</p><p><a href="https://www.wepe.com.cn/ubook/" target="_blank" rel="noreferrer">友情链接：微PE优盘使用说明书</a></p>',17)]))}const k=r(b,[["render",F]]);export{x as __pageData,k as default};
